import {useState} from 'react'

const BookCard = (props) => {

    const [styleStr, setStyleStr] = useState({
        transition: `.4s`,
        transformStyle: `preserve-3d`,
      });

      const rotateCard = () => {
        setStyleStr({
          transition: `.4s`,
          transformStyle: `preserve-3d`,
          transform: `rotateY(180deg)`,
        });
        setTimeout(() => {
          props.openPickedCard();
        }, 500);
      };


    return (
        <div
            className={"relative bg-mint rounded-xl w-[40%] h-[30%] m-[3%]"}
            style={styleStr}
            onClick={rotateCard}
        >
            <div
                className=""
            >

            </div>

        </div>
    )
}

export default BookCard
