        transition: `.4s`,
        transformStyle: `preserve-3d`,
      });

      const rotateCard = () => {
        setStyleStr({
          transition: `.4s`,
          transformStyle: `preserve-3d`,
          transform: `rotateY(180deg)`,
        });
        setTimeout(() => {
        }, 500);
      };

    return (
        <div
            className={"relative flex justify-center items-center bg-mint rounded-xl w-[40%] h-[30%] m-[3%]"}
            style={styleStr}
            onClick={rotateCard}
        >
            <div
                className="absolute bg-iceBlue w-[80%] h-[80%]"
                style={{backfaceVisibility: `hidden`}}
            >
                {props.word}
            </div>

            <div
                className="absolute bg-palePupple w-[80%] h-[80%]"
                style={{
                    transform: `rotateY(180deg)`,
                    backfaceVisibility: `hidden`
                }}
            >
                뒷면!!
            </div>
        </div>
    )
}

export default BookCard
