import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHouseChimney } from '@fortawesome/free-solid-svg-icons';
import { library } from '@fortawesome/fontawesome-svg-core';

import {useSelector} from 'react-redux';

library.add(faHouseChimney);

const BookModalHeader = () => {

    const textTitle = useSelector(state => state.bookText.text)

    return (
        <header className="w-full h-[17%] flex mt-[3%]">
            <div className="w-1/3 h-full">
            </div>
            <div className="w-1/3 h-full flex justify-center items-center">
                <div className="bg-modernOrange rounded-xl w-[70%] h-[70%] font-MaplestoryBold text-[200%] text-white flex justify-center item-center">
                    {textTitle}
                </div>
            </div>
            <div className="w-1/3 h-full flex justify-end item-start">
                {/* <button className="bg-mint w-[30%] h-full font-MaplestoryBold text-[200%] text-white rounded-full">
                    X
                </button> */}
               <button>
                <FontAwesomeIcon icon="fa-solid fa-circle-xmark" />
               </button>
            </div>
        </header>
    )
}

export default BookModalHeader;
