import {useState} from 'react'

const BookCard = (props) => {

    const [styleStr, setStyleStr] = useState({
        transition: `.4s`,
        transformStyle: `preserve-3d`,
      });
      const ratateCard = () => {
        setStyleStr({
          transition: `.4s`,
          transformStyle: `preserve-3d`,
          transform: `rotateY(180deg)`,
        });
        setTimeout(() => {
          props.openPickedCard();
        }, 500);
      };

    const [back, setBack] = useState(false)

    const [turn, setTurn] = useState(
        "bg-mint rounded-xl w-[40%] h-[30%] m-[3%]"
    )

    const cardHandler = () => {
        console.log(props.word)
        setTurn("bg-mint rounded-xl w-[40%] h-[30%] m-[3%] animate-rewardCard")
        setBack(true)
    }

    return (
        <div onClick={() => {cardHandler()}} className={turn}>
            {!back && <div>
                {props.word}
            </div>}
            {back && <div>똥강아지</div>}
        </div>
    )
}

export default BookCard
